  border-color: transparent rgba(##title-bg-color##) transparent transparent;
  right:auto !important;
  left:-10px !important;   
}
.ares.nav-dir-vertical.nav-pos-hor-left .tp-bullet.selected:hover .tp-bullet-title:after {
  border-color:  transparent rgba(##hover-bullet-bg-color##) transparent transparent !important;
}



