{"arrows":".hesperiden-2.tparrows {
	cursor:pointer;
	background:rgba(##bg-color##);
	width:##bg-size##px;
	height:##bg-size##px;
	position:absolute;
	display:block;
	z-index:100;
    border-radius: 50%;
}
.hesperiden-2.tparrows:hover {
	background:rgba(##hover-bg-color##);
}
.hesperiden-2.tparrows:before {
	font-family: \"revicons\";
	font-size:##arrow-size##px;
	color:rgb(##arrow-color##);
	display:block;
	line-height: ##bg-size##px;
	text-align: center;
}
.hesperiden-2.tparrows.tp-leftarrow:before {
	content: \"\\e82c\";
    margin-left:-3px;
}
.hesperiden-2.tparrows.tp-rightarrow:before {
	content: \"\\e82d\";
    margin-right:-3px;
}","bullets":".hesperiden-2.tp-bullets {
}
.hesperiden-2.tp-bullets:before {
	content:\" \";
	position:absolute;
	width:100%;
	height:100%;
	background:transparent;
	padding:10px;
	margin-left:-10px;margin-top:-10px;
	box-sizing:content-box;
   border-radius:8px;

}
.hesperiden-2 .tp-bullet {
	width:##bullet-size##px;
	height:##bullet-size##px;
	position:absolute;
	background: rgb(##bullet-bg-top##); /* old browsers */
    background: -moz-linear-gradient(top,  rgb(##bullet-bg-top##) 0%, rgb(##bullet-bg-bottom##) 100%); /* ff3.6+ */
    background: -webkit-linear-gradient(top,  rgb(##bullet-bg-top##) 0%,rgb(##bullet-bg-bottom##) 100%); /* chrome10+,safari5.1+ */
    background: -o-linear-gradient(top,  rgb(##bullet-bg-top##) 0%,rgb(##bullet-bg-bottom##) 100%); /* opera 11.10+ */
    background: -ms-linear-gradient(top,  rgb(##bullet-bg-top##) 0%,rgb(##bullet-bg-bottom##) 100%); /* ie10+ */
    background: linear-gradient(to bottom,  rgb(##bullet-bg-top##) 0%,rgb(##bullet-bg-bottom##) 100%); /* w3c */
    filter: progid:dximagetransform.microsoft.gradient( 
    startcolorstr=\"rgb(##bullet-bg-top##)\", endcolorstr=\"rgb(##bullet-bg-bottom##)\",gradienttype=0 ); /* ie6-9 */
	border:##border-size##px solid rgb(##border-color##);
	border-radius:50%;
	cursor: pointer;
	box-sizing:content-box;
}
.hesperiden-2 .tp-bullet:hover,
.hesperiden-2 .tp-bullet.selected {
	background:rgb(##hover-bullet-bg##);
}
.hesperiden-2 .tp-bullet-image {
}
.hesperiden-2 .tp-bullet-title {
}
","thumbs":".hesperiden-2 .tp-thumb {
  opacity:1;
  -webkit-perspective: 600px;
  perspective: 600px;
}
.hesperiden-2 .tp-thumb .tp-thumb-title {
    font-size:##title-font-size##px;
    position:absolute;
    margin-top:-10px;
    color:rgba(##title-color##);
    display:block;
    z-index:1000;
    background-color:rgba(##title-bg##);
    padding:5px 10px; 
    bottom:0px;
    left:0px;
    width:100%;
  box-sizing:border-box;
    text-align:center;
    overflow:hidden;
    white-space:nowrap;
    transition:all 0.3s;
    -webkit-transition:all 0.3s;
    transform:rotatex(90deg) translatez(0.001px);
    transform-origin:50% 100%;
    -webkit-transform:rotatex(90deg) translatez(0.001px);
    -webkit-transform-origin:50% 100%;
    opacity:0;
 }
.hesperiden-2 .tp-thumb:hover .tp-thumb-title {
  	 transform:rotatex(0deg);
    -webkit-transform:rotatex(0deg);
    opacity:1;
}","tabs":".hesperiden-2 .tp-tab { 
  opacity:1;      
  padding:10px;
  box-sizing:border-box;
  font-family: \"##font-family##\", sans-serif;
  border-bottom: ##border-size##px solid rgba(##border-color##);
 }
.hesperiden-2 .tp-tab-image 
{ 
  width:##image-size##px;
  height:##image-size##px; max-height:100%; max-width:100%;
  position:relative;
  display:inline-block;
  float:left;

}
.hesperiden-2 .tp-tab-content 
{
    background:rgba(##bgcolor##); 
    position:relative;
    padding:15px 15px 15px 85px;
 left:0px;
 overflow:hidden;
 margin-top:-15px;
    box-sizing:border-box;
    color:rgba(##contentcolor##);
    display: inline-block;
    width:100%;
    height:100%;
 position:absolute; }
.hesperiden-2 .tp-tab-date
  {
  display:block;
  color: rgba(##param1-color##);
  font-weight:500;
  font-size:##param1-size##px;
  margin-bottom:0px;
  }
.hesperiden-2 .tp-tab-title 
{
    display:block;	
    text-align:left	;
    color:rgba(##param2-color##);
    font-size:##param2-size##px;
    font-weight:500;
    text-transform:none;
    line-height:17px;
}
.hesperiden-2 .tp-tab:hover,
.hesperiden-2 .tp-tab.selected {
	background:rgba(##hover-bg-color##); 
}

.hesperiden-2 .tp-tab-mask {
}

